mapper.select:
SELECT


,chongwu.chongwu_name as chongwuName
,chongwu.chongwu_photo as chongwuPhoto
,chongwu.chongwu_address as chongwuAddress
,chongwu.zan_number as zanNumber
,chongwu.cai_number as caiNumber
,chongwu.chongwu_types as chongwuTypes
,chongwu.chongwu_clicknum as chongwuClicknum
,chongwu.chongwu_content as chongwuContent
,chongwu.chongwu_delete as chongwuDelete
,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_email as yonghuEmail
,yonghu.yonghu_delete as yonghuDelete

FROM chongwu_collection  a
left JOIN chongwu chongwu ON a.chongwu_id = chongwu.id
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



        and a.id in

        #{item}



        and (
            a.chongwu_id = #{params.chongwuId}
        )


        and (
            a.yonghu_id = #{params.yonghuId}
        )


        and a.chongwu_collection_types = #{params.chongwuCollectionTypes}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}




        and a.chongwu_id IS NOT NULL


        and chongwu.chongwu_name like CONCAT('%',#{params.chongwuName},'%')


        and chongwu.chongwu_address like CONCAT('%',#{params.chongwuAddress},'%')


          and chongwu.zan_number >= #{params.zanNumberStart}   


          and chongwu.zan_number <= #{params.zanNumberEnd}   


        and chongwu.zan_number = #{params.zanNumber}


          and chongwu.cai_number >= #{params.caiNumberStart}   


          and chongwu.cai_number <= #{params.caiNumberEnd}   


        and chongwu.cai_number = #{params.caiNumber}


        and chongwu.chongwu_types = #{params.chongwuTypes}



          and chongwu.chongwu_clicknum >= #{params.chongwuClicknumStart}   


          and chongwu.chongwu_clicknum <= #{params.chongwuClicknumEnd}   


        and chongwu.chongwu_clicknum = #{params.chongwuClicknum}


        and chongwu.chongwu_content like CONCAT('%',#{params.chongwuContent},'%')


          and chongwu.chongwu_delete >= #{params.chongwuDeleteStart}   


          and chongwu.chongwu_delete <= #{params.chongwuDeleteEnd}   


        and chongwu.chongwu_delete = #{params.chongwuDelete}


          and UNIX_TIMESTAMP(chongwu.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(chongwu.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}



        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


          and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}   


          and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}   


        and yonghu.yonghu_delete = #{params.yonghuDelete}


          and UNIX_TIMESTAMP(yonghu.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(yonghu.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}


order by a.${params.sort} ${params.order}
